import java.util.Scanner;

public class MainMethod {

    public static void main(String[] args) {

        //String str1 = "";
        //String str2 = "";

        String str1="ACACACTGACTACTGACTGGTGACTACTGACTGGACTGACTACTGACTGGTGACTACTGACTGG";
        String str2="TTATTATACGCGACGCGATTATACGCGACGCG";
        int l1 = str1.length();
        int l2 = str2.length();
        System.out.println(l1 + "    " + l2);
        int[][] dp = new int[l1+1][l2+1];
        int gapPenality = 30;
        int misMatchPenality = 0;
        int A = 0, C = 1, G = 2, T = 3;

        int[][] misMatchCost = {{0, 110, 48, 94}, {110, 0, 118, 48}, {48, 118, 0, 110}, {94, 48, 110, 0}};

        dp[0][0] = 0;
        for (int i=1;i<=l1;i++) dp[i][0]=dp[i-1][0]+gapPenality;
        for (int j=1;j<=l2;j++) dp[0][j]=dp[0][j-1]+gapPenality;
        for (int i = 1; i <= l1; i++) {
            for (int j = 1; j <= l2; j++) {
                if (str1.charAt(i - 1) == str2.charAt(j - 1)) misMatchPenality = 0;
                else {
                    if ((str1.charAt(i - 1) == 'A' || str2.charAt(j - 1) == 'A') && (str1.charAt(i - 1) == 'C' || str2.charAt(j - 1) == 'C'))
                        misMatchPenality = misMatchCost[A][C];
                    else if ((str1.charAt(i - 1) == 'A' || str2.charAt(j - 1) == 'A') && (str1.charAt(i - 1) == 'G' || str2.charAt(j - 1) == 'G'))
                        misMatchPenality = misMatchCost[A][G];
                    else if ((str1.charAt(i - 1) == 'A' || str2.charAt(j - 1) == 'A') && (str1.charAt(i - 1) == 'T' || str2.charAt(j - 1) == 'T'))
                        misMatchPenality = misMatchCost[A][T];
                    else if ((str1.charAt(i - 1) == 'C' || str2.charAt(j - 1) == 'C') && (str1.charAt(i - 1) == 'G' || str2.charAt(j - 1) == 'G'))
                        misMatchPenality = misMatchCost[C][G];
                    else if ((str1.charAt(i - 1) == 'C' || str2.charAt(j - 1) == 'C') && (str1.charAt(i - 1) == 'T' || str2.charAt(j - 1) == 'T'))
                        misMatchPenality = misMatchCost[C][T];
                    else if ((str1.charAt(i - 1) == 'G' || str2.charAt(j - 1) == 'G') && (str1.charAt(i - 1) == 'T' || str2.charAt(j - 1) == 'T'))
                        misMatchPenality = misMatchCost[G][T];
                }

                dp[i][j] = Math.min( dp[i-1][j-1]+misMatchPenality, Math.min(dp[i-1][j], dp[i][j-1])+gapPenality );
                //System.out.println("i :  "+i+"    j :    "+j);
            }
        }

        /*System.out.println(str1);
        System.out.println(str2);
        for (int i = 0; i <= l1; i++) {
            for (int j = 0; j <= l2; j++)
                System.out.print(dp[i][j] + "  ");
            System.out.println();
        }*/

        int len=l1+l2;
        int i=l1;   //current index of str1
        int j=l2;   //current index of str2

        int s1i = 49;
        int s2i = 49;

        char s1res[] = new char[50];
        char s2res[] = new char[50];

        int count=49;
        while (count>=0 &&i!= 0 && j!=0){

            if (str1.charAt(i-1)==str2.charAt(j-1)) misMatchPenality=0;
            else {
                if ((str1.charAt(i - 1) == 'A' || str2.charAt(j - 1) == 'A') && (str1.charAt(i - 1) == 'C' || str2.charAt(j - 1) == 'C'))
                    misMatchPenality = misMatchCost[A][C];
                else if ((str1.charAt(i - 1) == 'A' || str2.charAt(j - 1) == 'A') && (str1.charAt(i - 1) == 'G' || str2.charAt(j - 1) == 'G'))
                    misMatchPenality = misMatchCost[A][G];
                else if ((str1.charAt(i - 1) == 'A' || str2.charAt(j - 1) == 'A') && (str1.charAt(i - 1) == 'T' || str2.charAt(j - 1) == 'T'))
                    misMatchPenality = misMatchCost[A][T];
                else if ((str1.charAt(i - 1) == 'C' || str2.charAt(j - 1) == 'C') && (str1.charAt(i - 1) == 'G' || str2.charAt(j - 1) == 'G'))
                    misMatchPenality = misMatchCost[C][G];
                else if ((str1.charAt(i - 1) == 'C' || str2.charAt(j - 1) == 'C') && (str1.charAt(i - 1) == 'T' || str2.charAt(j - 1) == 'T'))
                    misMatchPenality = misMatchCost[C][T];
                else if ((str1.charAt(i - 1) == 'G' || str2.charAt(j - 1) == 'G') && (str1.charAt(i - 1) == 'T' || str2.charAt(j - 1) == 'T'))
                    misMatchPenality = misMatchCost[G][T];
            }

            if (str1.charAt(i-1)==str2.charAt(j-1)){
                i--;
                j--;
                s1res[count] = str1.charAt(i);
                s2res[count] = str2.charAt(j);
            }
            else if (dp[i][j]==dp[i-1][j-1]+misMatchPenality){
                i--;
                j--;
                s1res[count] = str1.charAt(i);
                s2res[count] = str2.charAt(j);
            }
            else if(dp[i][j]==dp[i-1][j]+gapPenality){
                i--;
                s1res[count]=str1.charAt(i);
                s2res[count] = '_';
            }
            else if(dp[i][j]==dp[i][j-1]+gapPenality){
                j--;
                s2res[count] = str2.charAt(j);
                s1res[count] = '_';
            }
            count--;

        }

        System.out.println(s1res);
        System.out.println(s2res);

    }
}
